mapper.select:
	   	select id, name, password, createDate
	   		from account
	   	
	   		
				and name like CONCAT('%',#{name},'%') 
			
        	
            	and password = #{password}
        	  	
        	
            	and createDate  >=  #{startTime}
        	
        	
            	and createDate  <=  #{endTime}
        	
        
        
        	limit ${(page -1) * limit}, ${limit}

	   	select count(id) from account
	   	
	   		
				and name like CONCAT('%',#{name},'%') 
			
        	
            	and password = #{password}
        	  	
        	
            	and createDate  >=  #{startTime}
        	
        	
            	and createDate  <=  #{endTime}
