mapper.select:
	   	select id, `name`, `desc`, createDate
	   		from role
	   	
	   		
				and name like CONCAT('%',#{name},'%') 
			
        	
            	and password = #{password}
        	  	
        	
            	and createDate  >=  #{startTime}
        	
        	
            	and createDate  <=  #{endTime}
        	
        
        
        	limit ${(page -1) * limit}, ${limit}

	   	select count(id) from role
	   	
	   		
				and name like CONCAT('%',#{name},'%') 
			
        	
            	and password = #{password}
        	  	
        	
            	and createDate  >=  #{startTime}
        	
        	
            	and createDate  <=  #{endTime}
